mapper.select:
SELECT code_id, site_id, comp_id, code_group, code, code_value, code_type, use_yn, code_ord, description, etc_info, reg_user_id, reg_dtm, upd_user_id, upd_dtm FROM tco_code WHERE 1 = 1 AND code_type = #{codeType}
AND code_group = #{codeGroup}
AND code_value like CONCAT('%',#{codeValue},'%')
ORDER BY code_group, code_ord

mapper.insert:
 INSERT INTO tco_code (
 	code_id, site_id, comp_id, code_group, code, code_value, 
		code_type, use_yn, code_ord, description, etc_info,
	    reg_user_id, reg_dtm, upd_user_id, upd_dtm
 )VALUES(
 	#{codeId}, #{siteId}, #{compId}, #{codeGroup}, #{code}, #{codeValue}, 
 	#{codeType}, #{useYn}, #{codeOrd}, #{description}, #{etcInfo},
 	#{currUserId}, #{currDateTime},#{currUserId}, #{currDateTime}
 )
