mapper.select:
        SELECT COALESCE(MAX(dept_ord),0) dept_ord
		FROM   tco_dept
        WHERE  1 = 1
        AND    comp_id   = #{compId}
        AND    p_dept_id = #{pDeptId}

mapper.update:
UPDATE tco_dept 
SET    dept_ord    = #{deptOrd} ,
	   upd_user_id = #{currUserId},
	   upd_dtm     = #{currDateTime}
WHERE  dept_id     = #{deptId}
